import React, { useEffect } from 'react';

declare global {
    interface Window {
        google: any;
    }
}

const Login: React.FC = () => {
    useEffect(() => {
        const initGoogleOneTap = () => {
            if (window.google && window.google.accounts && window.google.accounts.id) {
                console.log('Google One Tap initialized');
                window.google.accounts.id.initialize({
                    client_id: '506064022717-4f6o2i9v8560g5g3ns45h1al3sr2p8j0.apps.googleusercontent.com',
                    callback: handleCredentialResponse,
                });
                window.google.accounts.id.prompt(); // Ensure this is only called once
            } else {
                console.error('Google One Tap initialization failed');
            }
        };

        const handleCredentialResponse = (response: any) => {
            console.log('Encoded JWT ID token: ' + response.credential);
            // Send the token to your backend for verification
        };

        if (!document.getElementById('google-one-tap-script')) {
            const script = document.createElement('script');
            script.id = 'google-one-tap-script';
            script.src = 'https://accounts.google.com/gsi/client';
            script.async = true;
            script.defer = true;
            script.onload = () => {
                console.log('Google One Tap script loaded');
                initGoogleOneTap();
            };
            script.onerror = () => {
                console.error('Failed to load Google One Tap script');
            };
            document.body.appendChild(script);
        } else {
            initGoogleOneTap();
        }
    }, []);

    return (
        <div>
            <h2>Login</h2>
            <p>Please log in using Google One Tap.</p>
        </div>
    );
};

export default Login;
