import React, { useEffect } from 'react';

declare global {
    interface Window {
        google: any;
    }
}

const Login: React.FC = () => {
    useEffect(() => {
        const handleCredentialResponse = (response: any) => {
            console.log('Encoded JWT ID token: ' + response.credential);
            // Send the token to your backend for verification
            fetch('/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ token: response.credential }),
            })
                .then((res) => {
                    if (!res.ok) {
                        throw new Error('Login failed');
                    }
                    return res.json();
                })
                .then((data) => {
                    console.log('Login successful:', data);
                    // Handle successful login here (e.g., redirect to dashboard, store user info)
                })
                .catch((error) => {
                    console.error('Error during login:', error);
                    // Handle login error here (e.g., show error message)
                });
        // Check if the Google One Tap script is already loaded
        if (window.google && window.google.accounts && window.google.accounts.id) {
            console.log('Google One Tap already initialized');
            window.google.accounts.id.initialize({
                client_id: '506064022717-4f6o2i9v8560g5g3ns45h1al3sr2p8j0.apps.googleusercontent.com',
                callback: handleCredentialResponse,
                auto_select: true, // Automatically select the account if possible
            });
            window.google.accounts.id.prompt();
            return;
        };}

        const loadGoogleScript = () => {
            const script = document.createElement('script');
            script.src = 'https://accounts.google.com/gsi/client';
            script.async = true;
            script.defer = true;
            script.onload = () => {
                console.log('Google One Tap script loaded');
                window.google.accounts.id.initialize({
                    client_id: '506064022717-4f6o2i9v8560g5g3ns45h1al3sr2p8j0.apps.googleusercontent.com',
                    callback: handleCredentialResponse,
                    auto_select: true, // Automatically select the account if possible
                });
                window.google.accounts.id.prompt((notification: any) => {
                    if (notification.isNotDisplayed) {
                        console.error('Google One Tap not displayed:', notification.reason);
                    }
                    if (notification.isSkippedMoment) {
                        console.warn('Google One Tap skipped:', notification.reason);
                    }
                });
            };
            script.onerror = () => {
                console.error('Failed to load Google One Tap script');
            };
            document.body.appendChild(script);
        };

        if (!document.getElementById('google-one-tap-script')) {
            loadGoogleScript();
        }
    }, []);

    return (
        <div>
            <h2>Login</h2>
            <p>Google One Tap will appear automatically if you're eligible.</p>
        </div>
    );
};

export default Login;
