import React, { useEffect } from 'react';
import { API_URL, GOOGLE_CLIENT_ID } from '@/config';

declare global {
    interface Window {
        google: any;
    }
}

const Login: React.FC = () => {
    useEffect(() => {
        const handleCredentialResponse = (response: any) => {
            console.log('Encoded JWT ID token: ' + response.credential);
            // Send the token to your backend for verification
            fetch(`${API_URL}/loginGoogleToken`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ token: response.credential }),
            }).then((res) => {
                console.log('Response from backend:', res);
            });
        };

        const loadGoogleScript = () => {
            const script = document.createElement('script');
            script.src = 'https://accounts.google.com/gsi/client';
            script.async = true;
            script.defer = true;
            script.onload = () => {
                console.log('Google One Tap script loaded');
                window.google.accounts.id.initialize({
                    client_id: GOOGLE_CLIENT_ID,
                    callback: handleCredentialResponse,
                    auto_select: true, // Automatically select the account if possible
                });
                window.google.accounts.id.prompt((notification: any) => {
                    if (notification.isNotDisplayed) {
                        console.error('Google One Tap not displayed:', notification.reason);
                    }
                    if (notification.isSkippedMoment) {
                        console.warn('Google One Tap skipped:', notification.reason);
                    }
                });
            };
            script.onerror = () => {
                console.error('Failed to load Google One Tap script');
            };
            document.body.appendChild(script);
        };

        if (!document.getElementById('google-one-tap-script')) {
            loadGoogleScript();
        }
    }, []);

    return (
        <div>
            <h2>Login</h2>
            <p>Google One Tap will appear automatically if you're eligible.</p>
        </div>
    );
};

export default Login;
